import React from 'react';
import { MDBBtn, MDBCol, MDBContainer, MDBFile, MDBRow } from 'mdb-react-ui-kit';
import { MDBWysiwyg } from 'mdb-react-wysiwyg';
import { MDBFileUpload } from 'mdb-react-file-upload';

function App() {
  return (
    <MDBContainer fluid style={{backgroundColor: "#232323", color: "white"}}>
      <div
        className='d-flex justify-content-center'
        style={{ height: '100vh'}}
      >
        <div className='text-center'>
          <h3 className='mb-1 mt-4'>
            Cowlang IDE
          </h3>
          <p className='mb-0 pb-0'>A light-weight IDE made for FTC programming.</p>
          <MDBRow>
            <MDBCol>
              <MDBFileUpload style={{ height: '100px' }} acceptedExtensions={['.cow']} defaultMessage={"Got Milk? Upload your work."}/>
              <MDBWysiwyg className="rounded-5"/>
            </MDBCol>
          </MDBRow>
        </div>
      </div>
    </MDBContainer>
  );
}

export default App;
