import React, { useEffect } from "react";
import {
    MDBBtn,
    MDBBtnGroup,
    MDBCol,
    MDBContainer,
    MDBFile,
    MDBIcon,
    MDBInput,
    MDBModal,
    MDBModalBody,
    MDBModalContent,
    MDBModalDialog,
    MDBModalHeader,
    MDBModalTitle,
    MDBRow,
    MDBTabs,
    MDBTabsContent,
    MDBTabsItem,
    MDBTabsLink,
    MDBTabsPane,
} from "mdb-react-ui-kit";
import { MDBWysiwyg } from "mdb-react-wysiwyg";
import { MDBFileUpload } from "mdb-react-file-upload";

function App() {
    const [openFileUploadDialog, setOpenFileUploadDialog] =
        React.useState(false);

    const [fileName, setFileName] = React.useState("Moo");

    const [editorDimentions, setEditorDimentions] = React.useState({
        width: "800px",
        height: "500px",
    });

    const [mode, setMode] = React.useState("Line Code");

    return (
        <MDBContainer
            fluid
            style={{ backgroundColor: "#232323", color: "white" }}
        >
            <div
                className="d-flex justify-content-center"
                style={{ height: "100vh" }}
            >
                <MDBModal
                    dark
                    show={openFileUploadDialog}
                    setShow={setOpenFileUploadDialog}
                >
                    <MDBModalDialog>
                        <MDBModalContent style={{ backgroundColor: "#292929" }}>
                            <MDBModalHeader>
                                <MDBModalTitle style={{ color: "white" }}>
                                    Upload Cowlang File
                                </MDBModalTitle>
                            </MDBModalHeader>
                            <MDBModalBody>
                                {openFileUploadDialog && (
                                    <MDBFileUpload
                                        getInputFiles={(files) => {
                                            if (files.length === 0) {
                                                return;
                                            }

                                            setFileName(
                                                files[0].name.replace(
                                                    ".cow",
                                                    ""
                                                )
                                            );

                                            var read = new FileReader();
                                            read.readAsBinaryString(files[0]);

                                            read.onloadend = function () {
                                                document.getElementsByClassName(
                                                    "wysiwyg-content"
                                                )[0].innerText = read.result;
                                                setOpenFileUploadDialog(false);
                                            };
                                        }}
                                        disabledRemoveButton
                                        className="rounded-5"
                                        style={{
                                            height: "400px",
                                            backgroundColor: "#404040",
                                        }}
                                        acceptedExtensions={[".cow"]}
                                        defaultMessage={
                                            "Got Milk? Upload a .cow file here."
                                        }
                                    />
                                )}
                            </MDBModalBody>
                        </MDBModalContent>
                    </MDBModalDialog>
                </MDBModal>

                <div className="text-center">
                    <MDBBtn
                        outline
                        color="light"
                        style={{ position: "absolute", right: 0, top: 0, height: 50, border: "none", backgroundColor: "#404040", borderRadius: "0px" }}
                        onClick={() => {
                            setEditorDimentions(
                                editorDimentions.width != "95vw"
                                    ? { width: "95vw", height: "70vh" }
                                    : { width: "800px", height: "500px" }
                            );
                        }}
                    >
                        <MDBIcon icon="expand" className="me-2" />
                        {editorDimentions.width == "95vw"
                            ? "Shrink"
                            : "Expand"}{" "}
                        IDE
                    </MDBBtn>

                    <h3 className="mb-1 mt-4">Cowlang IDE</h3>
                    <p className="mb-0 pb-0">
                        A light-weight IDE made for FTC programming.
                    </p>
                    <MDBInput
                        className="mt-4"
                        label="Project Name"
                        contrast
                        value={fileName}
                        onChange={(e) => {
                            var newFileName = e.target.value;
                            setFileName(newFileName);
                        }}
                    />
                        <MDBTabs style={{position: "absolute", top: 0, left: 0}}>
                          <MDBTabsItem>
                            <MDBTabsLink
                                onClick={() => setMode("Line Code")}
                                style={{color: "white", backgroundColor: "#404040"}}
                                active={mode === "Line Code"}
                            >
                                Line Code
                            </MDBTabsLink>
                        </MDBTabsItem>
                        <MDBTabsItem>
                            <MDBTabsLink
                                onClick={() => setMode("Block Code")}
                                style={{color: "white", backgroundColor: "#404040"}}
                                active={mode === "Block Code"}
                            >
                                Block Code
                            </MDBTabsLink>
                        </MDBTabsItem>
                      </MDBTabs>

                    <MDBTabs>
                        <MDBTabsContent>
                            <MDBTabsPane show={mode === "Line Code"}>
                                <MDBWysiwyg
                                    id="editor"
                                    disableStyles
                                    disableFormatting
                                    disableLists
                                    disableLinks
                                    disableCode
                                    style={{
                                        height: editorDimentions.height,
                                        width: editorDimentions.width,
                                    }}
                                    className="rounded-5"
                                >
                                    <i style={{ color: "#bcf2a2" }}>
                                        '''Cowlang file'''
                                    </i>
                                </MDBWysiwyg>
                                <MDBContainer className="mt-4">
                                    <MDBBtn
                                        outline
                                        className="me-2"
                                        color="light"
                                        onClick={() => {
                                            setOpenFileUploadDialog(true);
                                        }}
                                    >
                                        <MDBIcon
                                            icon="upload"
                                            className="me-2"
                                        />
                                        Upload Work
                                    </MDBBtn>
                                    <MDBBtn
                                        outline
                                        id="down-btn"
                                        color="light"
                                        onClick={() => {
                                            var newFileName = fileName;
                                            if (!newFileName.endsWith(".cow")) {
                                                newFileName += ".cow";
                                            }

                                            var element =
                                                document.createElement("a");
                                            element.setAttribute(
                                                "href",
                                                "data:text/plain;charset=utf-8, " +
                                                    encodeURIComponent(
                                                        document.getElementById(
                                                            "editor"
                                                        ).innerText
                                                    )
                                            );
                                            element.setAttribute(
                                                "download",
                                                newFileName
                                            );

                                            document.body.appendChild(element);
                                            element.click();
                                            document.body.removeChild(element);
                                        }}
                                    >
                                        <MDBIcon
                                            icon="download"
                                            className="me-2"
                                        />
                                        Download "{fileName}.cow"
                                    </MDBBtn>
                                </MDBContainer>
                            </MDBTabsPane>
                            <MDBTabsPane
                                show={mode === "Block Code"}
                            ></MDBTabsPane>
                        </MDBTabsContent>
                    </MDBTabs>
                </div>
            </div>
        </MDBContainer>
    );
}

export default App;
