import React from 'react';
import { MDBBtn, MDBBtnGroup, MDBCol, MDBContainer, MDBFile, MDBIcon, MDBInput, MDBModal, MDBModalBody, MDBModalContent, MDBModalDialog, MDBModalHeader, MDBModalTitle, MDBRow } from 'mdb-react-ui-kit';
import { MDBWysiwyg } from 'mdb-react-wysiwyg';
import { MDBFileUpload } from 'mdb-react-file-upload';

function App() {

  const [openFileDialog, setOpenFileDialog] = React.useState(false);
  const [editorDimentions, setEditorDimentions] = React.useState({width: "800px", height: "500px"});

  const [fileName, setFileName] = React.useState("Moo");

  return (
    <MDBContainer fluid style={{backgroundColor: "#232323", color: "white"}}>
      <div
        className='d-flex justify-content-center'
        style={{ height: '100vh'}}
      >
        <MDBModal dark show={openFileDialog} setShow={setOpenFileDialog}>
          <MDBModalDialog>
            <MDBModalContent style={{backgroundColor: "#292929"}}>
              <MDBModalHeader>
                <MDBModalTitle style={{color: "white"}}>Upload Cowlang File</MDBModalTitle>
              </MDBModalHeader>
              <MDBModalBody>
              <MDBFileUpload className="rounded-5" style={{ height: '400px', backgroundColor: "#404040" }} acceptedExtensions={['.cow']} defaultMessage={"Got Milk? Upload a .cow file here."}/>
              </MDBModalBody>
            </MDBModalContent>
          </MDBModalDialog>
        </MDBModal>
        <div className='text-center'>
          <MDBBtn outline color='light' style={{position: "absolute", right: 25, top: 25}} onClick={() => {
            setEditorDimentions({width: "95vw", height: "80vh"});
          }}><MDBIcon icon="expand" className="me-2"/>Expand IDE</MDBBtn>
          <MDBBtn outline color='light' style={{position: "absolute", left: 25, top: 25}} onClick={() => {

          }}><MDBIcon icon="th-large" className="me-2"/>Write Block Code</MDBBtn>

          <h3 className='mb-1 mt-4'>
            Cowlang IDE
          </h3>
          <p className='mb-0 pb-0'>A light-weight IDE made for FTC programming.</p>
          <MDBRow style={{width: "200px"}}>
            <MDBCol size="10">
              <MDBInput contrast defaultValue={fileName} onChange={(e) => {setFileName(e.str)}}/>
            </MDBCol>
            <MDBCol size="2" className='mt-1'>
              <p>.cow</p>
            </MDBCol>
          </MDBRow>
          <MDBWysiwyg disableFormatting disableLists disableLinks disableCode style={{height: editorDimentions.height, width: editorDimentions.width}} className="rounded-5"/>
          <MDBContainer className="mt-4">
            <MDBBtn outline className="me-2" color='light' onClick = {() => {
              setOpenFileDialog(true);
            }}><MDBIcon icon="upload" className="me-2"/>Upload Work</MDBBtn>
            <MDBBtn outline color='light' download={".cow"}><MDBIcon icon="download" className="me-2"/>Download Work</MDBBtn>
          </MDBContainer>
        </div>
      </div>
    </MDBContainer>
  );
}

export default App;
