import React from 'react';
import { MDBBtn, MDBBtnGroup, MDBCol, MDBContainer, MDBFile, MDBModal, MDBModalBody, MDBModalContent, MDBModalDialog, MDBModalHeader, MDBModalTitle, MDBRow } from 'mdb-react-ui-kit';
import { MDBWysiwyg } from 'mdb-react-wysiwyg';
import { MDBFileUpload } from 'mdb-react-file-upload';

function App() {

  const [openFileDialog, setOpenFileDialog] = React.useState(false);

  return (
    <MDBContainer fluid style={{backgroundColor: "#232323", color: "white"}}>
      <div
        className='d-flex justify-content-center'
        style={{ height: '100vh'}}
      >
        <MDBModal dark show={openFileDialog} setShow={setOpenFileDialog}>
          <MDBModalDialog>
            <MDBModalContent style={{backgroundColor: "#292929"}}>
              <MDBModalHeader>
                <MDBModalTitle style={{color: "#292929"}}>Upload Cowlang File</MDBModalTitle>
                <MDBBtn className='btn-close' color='none' onClick={() => {setOpenFileDialog(false)}}></MDBBtn>
              </MDBModalHeader>
              <MDBModalBody>
              <MDBFileUpload className="rounded-5" style={{ height: '400px', backgroundColor: "#404040" }} acceptedExtensions={['.cow']} defaultMessage={"Got Milk? Upload a .cow file here."}/>
              </MDBModalBody>
            </MDBModalContent>
          </MDBModalDialog>
        </MDBModal>
        <div className='text-center'>
          <h3 className='mb-1 mt-4'>
            Cowlang IDE
          </h3>
          <p className='mb-0 pb-0'>A light-weight IDE made for FTC programming.</p>
          <MDBRow>
            <MDBCol>
              <MDBWysiwyg className="rounded-5"/>
              <MDBContainer>
                <MDBBtn outline className="mt-4 me-2" color='light' onClick = {() => {
                  setOpenFileDialog(true);
                }}>Upload Work</MDBBtn>
                <MDBBtn outline className="mt-4" color='light'>Download Work</MDBBtn>
              </MDBContainer>
            </MDBCol>
          </MDBRow>
        </div>
      </div>
    </MDBContainer>
  );
}

export default App;
