import React from 'react';
import { MDBBtn, MDBBtnGroup, MDBCol, MDBContainer, MDBFile, MDBModal, MDBModalBody, MDBModalTitle, MDBRow } from 'mdb-react-ui-kit';
import { MDBWysiwyg } from 'mdb-react-wysiwyg';
import { MDBFileUpload } from 'mdb-react-file-upload';

function App() {

  const [openFileDialog, setOpenFileDialog] = React.useState(false);

  return (
    <MDBContainer fluid style={{backgroundColor: "#232323", color: "white"}}>
      <div
        className='d-flex justify-content-center'
        style={{ height: '100vh'}}
      >
        <div className='text-center'>
          <h3 className='mb-1 mt-4'>
            Cowlang IDE
          </h3>
          <p className='mb-0 pb-0'>A light-weight IDE made for FTC programming.</p>
          <MDBRow>
            <MDBCol>
              <MDBWysiwyg className="rounded-5"/>
              <MDBContainer>
                <MDBBtn outline className="mt-4 me-2" color='light' onClick = {() => {
                  setOpenFileDialog(true);
                }}>Upload Work</MDBBtn>
                <MDBBtn outline className="mt-4" color='light'>Download Work</MDBBtn>
              </MDBContainer>
            </MDBCol>
          </MDBRow>
        </div>
      </div>
      <MDBModal dark show={openFileDialog} setShow={setOpenFileDialog}>
        <MDBModalDialog>
          <MDBModalTitle>Upload File</MDBModalTitle>
          <MDBModalBody>
          <MDBFileUpload className="mt-5 rounded-5" style={{ height: '100px', backgroundColor: "#404040" }} acceptedExtensions={['.cow']} defaultMessage={"Got Milk? Upload a .cow file here."}/>
          </MDBModalBody>
        </MDBModalDialog>
      </MDBModal>
    </MDBContainer>
  );
}

export default App;
