import React from 'react';
import { MDBBtn, MDBBtnGroup, MDBCol, MDBContainer, MDBFile, MDBIcon, MDBInput, MDBModal, MDBModalBody, MDBModalContent, MDBModalDialog, MDBModalHeader, MDBModalTitle, MDBRow } from 'mdb-react-ui-kit';
import { MDBWysiwyg } from 'mdb-react-wysiwyg';
import { MDBFileUpload } from 'mdb-react-file-upload';

function App() {

  const [openFileUploadDialog, setOpenFileUploadDialog] = React.useState(false);
  const [openFileDownloadDialog, setOpenFileDownloadDialog] = React.useState(false);

  const [fileName, setFileName] = React.useState("Moo.cow");

  const [editorDimentions, setEditorDimentions] = React.useState({width: "800px", height: "500px"});

  return (
    <MDBContainer fluid style={{backgroundColor: "#232323", color: "white"}}>
      <div
        className='d-flex justify-content-center'
        style={{ height: '100vh'}}
      >

        <MDBModal dark show={openFileUploadDialog} setShow={setOpenFileUploadDialog}>
          <MDBModalDialog>
            <MDBModalContent style={{backgroundColor: "#292929"}}>
              <MDBModalHeader>
                <MDBModalTitle style={{color: "white"}}>Upload Cowlang File</MDBModalTitle>
              </MDBModalHeader>
              <MDBModalBody>
              <MDBFileUpload className="rounded-5" style={{ height: '400px', backgroundColor: "#404040" }} acceptedExtensions={['.cow']} defaultMessage={"Got Milk? Upload a .cow file here."}/>
              </MDBModalBody>
            </MDBModalContent>
          </MDBModalDialog>
        </MDBModal>

        {/* <MDBModal dark show={openFileDownloadDialog} setShow={setOpenFileDownloadDialog}>
          <MDBModalDialog>
            <MDBModalContent style={{backgroundColor: "#292929"}}>
              <MDBModalHeader>
                <MDBModalTitle style={{color: "white"}}>Download .cow File</MDBModalTitle>
              </MDBModalHeader>
              <MDBModalBody>
                <MDBInput className="mt-4" label="File Name" id="file-name-input" contrast defaultValue="Moo.cow"/>
                <MDBBtn className="mt-4" outline color='light' onClick = {() => {
                  const fileName = document.getElementById("file-name-input").value;
                  const file = new Blob([document.getElementById("editor").innerHTML], {type: 'text/plain'});
                  const url = URL.createObjectURL(file);
                  //open url

                }}><MDBIcon icon="download" className="me-2"/>Download '{document.getElementById("file-name-input").value}'</MDBBtn>
              </MDBModalBody>
            </MDBModalContent>
          </MDBModalDialog>
        </MDBModal> */}

        <div className='text-center'>
          <MDBBtn outline color='light' style={{position: "absolute", right: 25, top: 25}} onClick={() => {
            setEditorDimentions({width: "95vw", height: "80vh"});
          }}><MDBIcon icon="expand" className="me-2"/>Expand IDE</MDBBtn>
          <MDBBtn outline color='light' style={{position: "absolute", left: 25, top: 25}} onClick={() => {

          }}><MDBIcon icon="th-large" className="me-2"/>Write Block Code</MDBBtn>

          <h3 className='mb-1 mt-4'>
            Cowlang IDE
          </h3>
          <p className='mb-0 pb-0'>A light-weight IDE made for FTC programming.</p>
          <MDBWysiwyg id="editor" disableFormatting disableLists disableLinks disableCode style={{height: editorDimentions.height, width: editorDimentions.width}} className="rounded-5"/>
          <MDBContainer className="mt-4">
            <MDBBtn outline className="me-2" color='light' onClick = {() => {
              setOpenFileUploadDialog(true);
            }}><MDBIcon icon="upload" className="me-2"/>Upload Work</MDBBtn>
            <MDBBtn outline color='light' onClick = {() => {
              
            }}><MDBIcon icon="download" className="me-2"/>Download Work</MDBBtn>
          </MDBContainer>
        </div>
      </div>
    </MDBContainer>
  );
}

export default App;
